import React, { Component } from 'react';
import toastr from '../notification/Toastr'

//COMPONENTS
import Order from './Order';
import EditDestination from './EditDestination';
import CreateOrder from './CreateOrder';

export class Orders extends Component {

state = { 
    data:{
        orderList:[],
        showEdit:"close",
        showCreate:"close"


    },
};



componentDidMount(){
const token = sessionStorage.getItem('token');
if(!token){
    toastr.Warning("YOU ARE NOT LOGGED IN");
    this.props.history.push('/login');

}else if(token){

    fetch('https://s-i-api.herokuapp.com/api/v1/order',{

        headers:{
            Authorization: token
        }

    }).then(res=>{return res.json()})
    .then(data=>{
        if(data.length === 0){

            toastr.info('YOUR ORDERS LISTS IS STILL EMPTY')

        }else {

            const newData = {...this.state.data};
            newData.orderList = data;
            this.setState({
                data: newData
            });

            console.log(this.state)

        }

    })


}


};

handleUpdateOrder=(data)=>{
    const date = Date.now();
    const dateObj= new Date(date) 
    const day = dateObj.getDate();
    const month = dateObj.getMonth();
    const year = dateObj.getFullYear();
    const dateString = `${year}-${month}-${day}T `

    const dataCopy = {...this.state.data};
    data.date = dateString;
    const newList = [...dataCopy.orderList, data];
                dataCopy.orderList = newList;
    this.setState({
            data: dataCopy
    })
    const token = sessionStorage.getItem('token');
    const dataTrans = { dataCopy}
        dataTrans.date = date;
        fetch('https://s-i-api.herokuapp.com/api/v1/new_order',{
            method:"POST",
            headers:{
                "Content-Type":"application/json",
                Authorization: token
            },
            body:JSON.stringify(dataTrans)
        })
            .then(res=>{
                toastr.success("NEW ORDER ADDED TO ORDER LIST")
            })


}







handleClickCreate=()=>{

    const newData = {...this.state.data};
    if(newData.showCreate === "close"){
    newData.showCreate="open";
    this.setState({
        data: newData
    });
}else if(newData.showCreate === "open"){
    newData.showCreate ="close";
    this.setState({
        data: newData
    });
}

}






handleClickEdit=()=>{

    const newData = {...this.state.data};
    if(newData.showEdit === "close"){
    newData.showEdit="open";
    this.setState({
        data: newData
    });
}else if(newData.showEdit === "open"){
    newData.showEdit="close";
    this.setState({
        data: newData
    });
}

}



  render() {
    return (
      <div className="orders">
            <h3 className="orders-title">LIST OF ORDERS AND DELIVERY DETAILS</h3>
            <Order orderList={this.state.data.orderList}  />
            <button onClick={this.handleClickEdit} >EDIT DESTINATION</button>
            <button  onClick={this.handleClickCreate}>CREATE ORDER</button>
            <EditDestination  show={this.state.data.showEdit}/>
            <CreateOrder show={this.state.data.showCreate} updateOrder={handleUpdateOrder} />


      </div>
    )
  }
}

export default Orders;
