            <div>
            <label htmlFor="password" >PASSWORD:</label>
            <input id="password" type="password" onChange={this.handleChange} placeholder="ENTER PASSWORD" required/>
            </div>
            <button disabled={this.state.data.chck} type="submit">LogIn</button>
        </form>
      </div>
    );
    }
  }

