import React,{ Component } from 'react';
import { Link } from 'react-router-dom';


//STYLES
import '../styles/Form.css'

class Login extends Component {

    AdminSlide =()=>{
        const chk = window.confirm('RESTRICTED ADMIN ONLY !!!');
            if(chk == false){
                return
            }else{
                console.log(this.props)
                this.props.history.push('/adminlogin')
            }

    }


    render(){

    return (
      <div className="login">
        <form className="user-login" >
            <div>
            <p>Dont't Have An Account? Go To Signup<span onClick={this.AdminSlide} id="adbtn">AD</span></p>
            <label htmlFor="email">EMAIL:</label>
            <input name="email" type="text" placeholder="ENTER EMAIL" required/>
            </div>
            <div>
            <label htmlFor="password" >PASSWORD:</label>
            <input name="password" type="password" placeholder="ENTER PASSWORD" required/>
            </div>
            <button type="submit" title="SUBMIT">LogIn</button>
        </form>
      </div>
    );
    }
  }

  export default Login;
