import React from 'react';

const OrderSumary = () => {

    const token = sessionStorage.getItem('token');  
    fetch('https://s-i-api.herokuapp.com/api/v1/order',{

        headers:{
            Authorization: token
        }

    }).then(res=>{return res.json()})
        .then(list=>{

            let deliveredCount= 0;
            let intransitCount = 0;
            list.forEach(order=>{
                if(order.status == 'Delivered' ){
                    deliveredCount ++;
                }else if( order.status == 'Intransit'|| list.status == 'In transit' ){
                    intransitCount++;
                }
            })

        }).catch(err=>console.log(err));



    return (
        <div className="order_sumary">
            <div className="ord_sum_deliv">
                <p className="ord_sum_deliv_title">ORDERS DELIVERED</p>
                <p className="ord_sum_deliv_value">{deliveredCount}</p>
            </div>
            <div className="ord_sum_intran">
                <p className="ord_sum_intran_title">ORDERS IN PROCESS</p>
                <p className="ord_sum_intran_value">{intransitCount}</p>
            </div>

        </div>
    );
}

export default OrderSumary;
