import React, { Component } from 'react'

export class AdminEdit extends Component {
  state={
          data:{
              ord:{
                userId: "",
                orderId: "",
                orderStatus:"",
                orderLocation:"",
                type:""
              },
              class:"closed"

          }
  }


handleClickA=(e)=>{
  console.log(this.state.data.class,  e.target.classList.value)
    if(this.state.data.class === "closed" && e.target.classList.value === 'status_tog'){
      const dataCopy = {...this.state.data};
      dataCopy.class = "open_status";
      this.setState({
          data: dataCopy
      });
    }else if(this.state.data.class === 'open_status' && e.target.classList.value === 'status_tog'){
      const dataCopy = {...this.state.data};
      dataCopy.class = "closed";
      this.setState({
          data: dataCopy
      });
    }else{
      console.log("ERROR IN CLASS CHANGE",this.state.data.class,  e.target.classList.value)
    }
}


handleClickB=(e)=>{
  if(this.state.data.class === 'closed' && e.target.classList.value === 'location_tog'){
    const dataCopy = {...this.state.data};
    dataCopy.class = "open_location";
    this.setState({
        data: dataCopy
    });
  }else if(this.state.data.class === 'open_location' && e.target.classList.value === 'location_tog'){
    const dataCopy = {...this.state.data};
    dataCopy.class = "closed";
    this.setState({
        data: dataCopy
    });
  }else{
    console.log("ERROR IN CLASS CHANGE")
  }


}


 componentDidCatch(){


   // this.props.edit_on_form(this)



/*
  const dataCopy = {...this.state.data};
  if(this.props.payload){
    dataCopy.ord.ordId = this.props.payload.ordId;
    dataCopy.ord.userId = this.props.payload.userIdentity;
    switch(this.props.payload.type){
      case 'statu':
        dataCopy.ord.type  = 'status';
        break;
      case 'locatn': 
        dataCopy.ord.type  =  'location';
        break;
      default:
        return
    };
      if(this.state.data.class = 'closed'){
          const copydata = {...this.state.data};
          copydata.class = 'open';
        this.setState({ data: copydata})

      }else if(this.state.data.class = 'open'){
        const copydata = {...this.state.data};
          copydata.class = 'closed';
        this.setState({ data: copydata})
      }
      console.log(this.state)
  } */

}



  render() {
    return (
      <div className="admin_edit">
        <div className="tog_button">
          <button className="status_tog" onClick={this.handleClickA}>STATUS EDIT</button>
          <button className="location_tog" onClick={this.handleClickB}>LOCATION EDIT</button>
        </div>
        <form className={"curent_location_form "+ this.state.data.class} >
          <label htmlFor="user_identity">USER ID</label>
          <input type="text" placeholder="ENTER USER ID " id="user_identity"/>
          <label htmlFor="order_new_location">NEW LOCATION</label>
          <input type="text" placeholder="ENTER THE CURRENT LOCATION " id="user_identity"/>
          <label htmlFor="order_identity">ORDER ID</label>
          <input type="text" placeholder="ENTER USER ID " id="user_identity"/>
          <button type="submit" className="admin_submit">UPDATE </button>
        </form>
        <form action="" className={"status_form "+ this.state.data.class}>
          <label htmlFor="user_ordr_f2">USER ID</label>
          <input type="text" placeholder="ENTER USER ID"/>
          <label htmlFor="order_id_f2">ORDER ID</label>
          <input type="number" placeholder="ENTER ORDER ID"/>
          <select name="" id="status_option">
            <option value="Delivered">Delivered</option>
            <option value="Intransit">Intransit</option>
          </select>
          <button type="submit" className="admin_submit">UPDATE </button>
        </form>
      </div>
    )
  }
}

export default AdminEdit
