    })
}




    render(){

    return (
      <div className="signup">
        <form className="user-login"  > 
            <div>
            <p>Have An Account? Go To Login</p>
                <label htmlFor="username">USERNAME: </label>
                <input id="username" type="text" placeholder="ENTER USER NAME" onChange={this.updateState} required/>
            </div>
            <div>
            <label htmlFor="phoneno" >PHONE NO: </label>
            <input id="phoneno" type="number" placeholder="ENTER PHONE NUMBER" required/>
            </div>
            <div>
            <label htmlFor="house_address" >HOUSE ADDRESS:</label>
            <input id="house_address" type="text" placeholder="ENTER HOUSE ADDRESS"/>
            </div>
            <div>
                <label htmlFor="email">EMAIL:</label>
                <input  id="email" type="email" placeholder="ENTER EMAIL" required/>
            </div>
            <div>
                <label htmlFor="password" >PASSWORD:</label>
                <input  id="password" placeholder="ENTER PASSWORD" type="password" required/>
            </div>
            <button type="submit" title="CREATE ACCOUNT">Create</button>
        </form>
      </div>
    );
    }
  }

  export default Signup;
