import React, { Component } from 'react'

 class Acc_edit extends Component {
  render() {
    let impType;
    switch(this.props.typ){
        case 'username':
        case 'addres':
          impType='text';
          break;
        case 'password':
          impType= 'password';
          break;
        case 'number':
          impType= 'number'
        break;
        default:
          impType='text';

    };
    console.log(impType)


    return (
      <div className="acc_edit" id={this.props.show}>
    <h3 className="title">REPLACE{this.props.field}</h3>
      <input type={impType} placeholder="ENTER THE NEW CONTENT" className="edit-i"/>
      <button type="submit" className="editsub">UPDATE</button>
      </div>
    )
  }
}

export default Acc_edit
