import React, { Component } from 'react'

 class Acc_edit extends Component {


  state = {
          data:{
            newVal:"",
            keyToValue:""
          }
  };



handleChange=(e)=>{

const keyToValue = this.props.typ;
const val = e.target.value;
const data= {
      val, keyToValue
}

this.setState({
          data
});
console.log(this.state)
}


  render() {
    let impType;
    switch(this.props.typ){
        case 'user-name':
        case 'house-address':
          impType='text';
          break;
        case 'password':
          impType= 'password';
          break;
        case 'phone-number':
          impType= 'number'
        break;
        default:
          impType='text';

    };

    return (
      <div className="acc_edit" id={this.props.show}>
    <h3 className="title">Replace {this.props.typ}</h3>
      <input type={impType} placeholder="ENTER THE NEW CONTENT" className="edit-i" onChange={this.handleChange}/>
      <button type="submit" className="editsub">UPDATE</button>
      </div>
    )
  }
}

export default Acc_edit
