import React, { Component } from 'react'



export class EditDestination extends Component {

  state= {
      data:{
          orderUpdate:{
            upDestnAddress:"",
            ordId:""
          }
      }
  }

handleChange=(e)=>{
  const copyData= {...this.state.data};
  copyData.orderUpdate[e.target.id] = e.target.value;
  this.setState({
          data: copyData
  })

}

handleSubmit=(e)=>{
e.preventDefault();
    this.props.handleDestinationUpdate(this.state.data.orderUpdate);

}

  render() {
    console.log(this.props)
    return (
      <div  className={'edit-destination '+ this.props.show}>
          <form className="order-edit"  onSubmit={this.handleSubmit} >
              <div>
              <h3>EDIT DESTINATION</h3>
              <label htmlFor="upDestnAddress">NEW DESTINATION</label>
              <input id="upDestnAddress" type="text" onChange={this.handleChange} value={this.state.data.orderUpdate.upDestnAddress} placeholder="ENTER NEW DESTINATION ADDRESS"/>
              </div>
              <div>
              <label htmlFor="ordId" >ORDER:</label>
              <input id="ordId" type="number" onChange={this.handleChange} value={this.state.data.orderUpdate.ordId} placeholder="ENTER ORDER ID"  />
              </div>
              <button  type="submit" title="SUBMIT">UPDATE</button>
          </form>
      </div>
    )
  }
}

export default EditDestination
