import React, { Component } from 'react'
import toastr from '../notification/Toastr' 


//COMPONENTS
import AdminOrdersList from './AdminOrdersList';
import AdminEdit from './AdminEdit';


//STYLES
import '../styles/AdminComp.css'

class AdminComp extends Component {

    state = { 
        data:{
            orderList:[],
            payload:{}

        },
    };


    handleAdminEdit=(payload)=>{
        const dataCopy = {...this.state.data}
        dataCopy.payload = payload;
        this.setState({ data : dataCopy})

    }

handleEditOnForm=(obj)=>{

    const dataCopy = {...obj.state.data};

    dataCopy.ord.ordId = this.state.data.payload.ordId
    dataCopy.ord.userId = this.state.data.payload.userIdentity;
    switch(this.state.data.payload.type){
      case 'statu':
        dataCopy.ord.type  = 'status';
        break;
      case 'locatn': 
        dataCopy.ord.type  =  'location';
        break;
      default:
        return
    };
    obj.setState({data: dataCopy})
        console.log(this.state.data)


}


componentDidMount(){

    const adminToken = sessionStorage.getItem('adminToken');

    if(!adminToken){
        toastr.warning("CAN'T FIND ADMIN CREDENTIALS !!!");
        this.props.history.push('/login')
    }else {

    fetch('https://s-i-api.herokuapp.com/api/v1/admin_orders',{
        headers:{
            Authorization: adminToken
        }

    })
        .then(res=>{
        return res.json();
    })
        .then(data=>{
            const datacopy={...this.state.data};
                datacopy.orderList=data;
                this.setState({
                    data: datacopy
                })
            console.log(this.state)
        })
        .catch(err=>{toastr.error('ERROR FETCHING THE ORDERS LISTS')})
};
}

  render() {
    return (
      <div className="admin_comp">
          <h4 className="all_orders_title">
              ALL ORDERS LIST WITH CORRESPONDING USER
          </h4>
            <AdminOrdersList orders_list={this.state.data.orderList}
             handleAdminEdit={this.handleAdminEdit} />
            <AdminEdit edit_on_form={this.handleEditOnForm} />
      </div>
    )
  }

}

export default AdminComp
