import React,{ Component } from 'react';


//STYLES
//import '../styles/Form.css'

class Signup extends Component {

state ={
            data:{
        user:{
        username:"",
        phoneNumber:"",
        houseAddress:"",
        email:"",
        password:""
        },
        chksub:false
    }
};


updateState=(e)=>{
const newData = {...this.state.data};
let infoField;

switch(e.target.id){
    case 'username':
        infoField='username';
    break;
    case 'phoneno':
        infoField='phoneNumber';
    break;
    case 'house_address':
        infoField='houseAddress';
    break;
    case 'email':
        infoField='email';
    break;
    case 'password':
        infoField='password';
    break;
    default:
        return

}



newData.user[infoField] = e.target.value;
    this.setState({
            data: newData
    });
}

formAction=(e)=>{
    e.preventDefault();
    console.log(this.state)
    const newData = {...this.state.data};
    newData.chksub= true;
    this.setState({
            data: newData
    });

} 


    render(){

    return (
      <div className="signup">
        <form className="user-login"  onSubmit={this.formAction}> 
            <div>
            <p>Have An Account? Go To Login</p>
                <label htmlFor="username">USERNAME: </label>
                <input id="username" type="text" placeholder="ENTER USER NAME" onChange={this.updateState}  minLength="6" maxLength="20" required/>
            </div>
            <div>
            <label htmlFor="phoneno" >PHONE NO: </label>
            <input id="phoneno" type="number" placeholder="ENTER PHONE NUMBER" onChange={this.updateState} minLength="8" maxLength="20" required/>
            </div>
            <div>
            <label htmlFor="house_address" >HOUSE ADDRESS:</label>
            <input id="house_address" type="text" placeholder="ENTER HOUSE ADDRESS " onChange={this.updateState}/>
            </div>
            <div>
                <label htmlFor="email">EMAIL:</label>
                <input  id="email" type="email" placeholder="ENTER EMAIL" onChange={this.updateState} required/>
            </div>
            <div>
                <label htmlFor="password" >PASSWORD:</label>
                <input  id="password" placeholder="ENTER PASSWORD" type="password" onChange={this.updateState} minLength="8" maxLength="20" required/>
            </div>
            <button disabled={this.state.data.chksub} type="submit" title="CREATE ACCOUNT">Create</button>
        </form>
      </div>
    );
    }
  }

  export default Signup;
